mapper.select:
SELECT
e.id, e.name, e.description, e.time, e.start_time, e.end_time, e.create_user, e.role_id, e.score, e.status,
e.create_time, e.last_modify_time,us.`name` create_user_name,s.`name` role_name
FROM exam e LEFT JOIN `user` us ON e.`create_user`=us.`user_id`
LEFT JOIN s_role s ON e.`role_id`=s.`role_id`


      e.status = #{status}


        e.name LIKE concat("%",#{name},"%")


        e.create_user = #{userId}


        e.role_id = #{roleId}


        e.score = #{score}



        e.start_time >= #{startTime}




        e.end_time <= #{endTime}



ORDER BY e.create_time DESC

    LIMIT #{rowStart}, #{rowCount}

select

from exam


        status = #{status}


        name LIKE concat("%",#{name},"%")


        create_user = #{userId}


        role_id = #{roleId}


        score = #{score}



        start_time >= #{startTime}




        end_time <= #{endTime}



ORDER BY create_time DESC

    LIMIT #{rowStart}, #{rowCount}

select
count(1)
from exam


        status = #{status}


        name LIKE concat("%",#{name},"%")


        create_user = #{userId}


        role_id = #{roleId}


        score = #{score}



